// ! Correct implementation: the only one that works for all datasets
// Sorts the array.
// Fixes one element and then does two-pointer 2-sum on the remaining elts
func threeSum_two_pointer_with_fixed(nums []int) (result [][]int) {
	if len(nums) < 3 {
		return
	}

	slices.Sort(nums)
	// fmt.Printf("sorted: %v\n", nums)

	for i := 0; i < len(nums)-2; {
		fixed_num := nums[i]

		begin := i + 1
		end := len(nums) - 1
		for begin < end {
			l := nums[begin]
			r := nums[end]
			target := -fixed_num

			switch cmp.Compare(target, l+r) {
			case 0:
				result = append(result, []int{nums[i], l, r})

				// skip duplicate numbers on the left
				for begin++; begin < end && nums[begin] == l; begin++ {
				}

				// skip duplicate numbers on the right
				for end--; end >= begin && nums[end] == r; end-- {
				}
			case 1: // target < l+r, need to reduce the sum => move r
				begin++
			case -1: // target > l+r, need to increase the sum => move l
				end--
			}
		}

		// loop increment: skip duplicates of the fixed number
		for i++; i < len(nums)-2 && nums[i] == fixed_num; i++ {
		}
	}
	return
}

// ! This does not work for all possible cases
// Tries to do plain two pointers and bisecting elts between them
// flawed, because it can't find all the combinations
// i have no idea how to move the side pointers correctly
func threeSum_two_pointer_from_sides(nums []int) (result [][]int) {
	slices.Sort(nums)

	fmt.Printf("sorted: %v\n", nums)

	dedup := make(map[[3]int]struct{})

	begin := 0
	end := len(nums) - 1

	turn := 0

	for begin < end {
		l := nums[begin]
		r := nums[end]

		lookfor := 0 - (l + r)
		idx, found := slices.BinarySearch(nums[begin+1:end], lookfor)

		idx = begin + 1 + idx // adjust to be in the full nums interval

		fmt.Printf("l: %d, r: %d, look: %d -> x: %d, idx: %d, found: %v\n",
			l, r, lookfor, nums[idx], idx, found)

		if found {
			k := [3]int{l, nums[idx], r}
			if _, ok := dedup[k]; !ok {
				result = append(result, k[:])
				dedup[k] = struct{}{}
			}
		}

		if turn == 0 {
			begin++
			turn = 1
		} else {
			end--
			turn = 0
		}
	}
	return
}

// ! Bad (even though correct) implementation: too slow, because it is double quadratic
//  1. make the sums map
//  2. for each value - look into the sums map and then iterate over everything
//     (that is already quadratic by itself, kekw)
func threeSum_with_hash(nums []int) (result [][]int) {
	if len(nums) < 3 {
		return
	}

	slices.Sort(nums)
	// fmt.Printf("sorted: %v\n", nums)

	// pair sum map; sum -> [][index1, index2]
	sums := make(map[int][][2]int)
	for i := range len(nums) {
		for j := i + 1; j < len(nums); j++ {
			sums[nums[i]+nums[j]] = append(sums[nums[i]+nums[j]], [2]int{i, j})
		}
	}

	dedup := make(map[[3]int]struct{})

	// for k, v := range sums {
	// 	fmt.Printf(" [%d] %v\n", k, v)
	// }

	for i, v := range nums {
		indexes, ok := sums[-v]
		// fmt.Printf("i: %d -> %v; sums[%d]: %v\n", i, v, -v, indexes)
		if !ok {
			continue
		}

		for _, jv := range indexes {
			if i == jv[0] || i == jv[1] {
				continue
			}
			key := [3]int{nums[i], nums[jv[0]], nums[jv[1]]}
			slices.Sort(key[:])
			if _, ok := dedup[key]; !ok {
				dedup[key] = struct{}{}
				result = append(result, key[:])
				// fmt.Printf("  result_add: %v\n", key[:])
			}
		}
	}

	return
}

func sortExpected(xs [][]int) [][]int {
	slices.SortFunc(xs, func(l, r []int) int { return slices.Compare(l, r) })
	return xs
}

